  data () {
    return {
      msg: 'SOME COOL MESSAGE',
      // pathElements: document.getElementById('vermont').childNodes[0].childNodes[0].childNodes
    }
  },
  methods: {
    log () {
      console.log(this.msg);
      console.log(state.districts);
    },

    main() {
      for (let i in state.districts) {
        const district = state.districts[i]
        console.log(district.successIndex);
        this.display(district.svgId, district.successIndex)
      }
    },

    display (id, successIndex) {
      let pathElements = document.getElementById('vermont').childNodes[0].childNodes[0].childNodes

      for (let path of pathElements) {
        if (path.id === id) {
          path.setAttribute('fill-opacity', 1)
          let fillVal = successIndex === 420 ? 'rgba(0,0,0,0.3)' : this.calculateColor(successIndex)
          console.log(fillVal);
          path.setAttribute('fill', fillVal)
        }
      }
    },

    calculateColor(successIndex) {
      let opacity = Math.abs(successIndex - 0.5) * 2 + 0.1
      let color = successIndex > 0.5 ? '49, 146, 181' : '164, 14, 14'

      return `rgba(${color}, ${opacity})`
    }
  }
}
